const getRandomNumber = (min, max) => Math.floor(Math.random() * (max - min + 1)) + min;

const getRandomString = (length) => {
  const chars = 'ABCDEFGHIJKLMNOPQRSTUVWXYZabcdefghijklmnopqrstuvwxyz';
  return Array.from({ length }, () => chars[getRandomNumber(0, chars.length - 1)]).join('');
};

function getRandomElement(array) {
  return array[Math.floor(Math.random() * array.length)];
}

const getRandomZip = () => String(getRandomNumber(10000, 99999)); 

function getRandomName() {
  const firstNames = ['John', 'Jane', 'Michael', 'Emily', 'Chris', 'Sarah', 'David', 'Laura'];
  const lastNames = ['Smith', 'Johnson', 'Brown', 'Williams', 'Jones', 'Garcia', 'Miller', 'Davis'];
  return `${getRandomElement(firstNames)} ${getRandomElement(lastNames)}`;
}

function getRandomStreet() {
  const streetNames = ['Main', 'High', 'Oak', 'Maple', 'Pine', 'Elm', 'Cedar', 'Birch'];
  const streetTypes = ['St', 'Ave', 'Blvd', 'Rd', 'Ln'];
  return `${getRandomNumber(100, 999)} ${getRandomElement(streetNames)} ${getRandomElement(streetTypes)}`;
}

function getRandomCountry() {
  const countries = ['USA', 'Canada', 'UK', 'Germany', 'France', 'Australia', 'India'];
  return getRandomElement(countries);
}

const getRandomExpiryDate = () => {
  const currentYear = new Date().getFullYear();
  const expiryYear = getRandomNumber(currentYear + 1, currentYear + 5);
  const expiryMonth = getRandomNumber(1, 12); 
  return `${expiryMonth < 10 ? '0' : ''}${expiryMonth}/${expiryYear % 100}`; // MM/YY like for ex: 01/24
};

const generateCVV = (issuer) => {
  switch (issuer) {
    case 'American Express':
      return getRandomNumber(1000, 9999); 
    case 'Visa':
    case 'MasterCard':
    case 'Discover':
      return getRandomNumber(100, 999); 
  }
};

const generateCardNumber = (issuer) => {
  let prefix = '';
  let length = 0;

  switch (issuer) {
    case 'Visa':
      prefix = '4';
      length = 16;
      break;
    case 'MasterCard':
      prefix = `5${getRandomNumber(1, 5)}`;
      length = 16;
      break;
    case 'American Express':
      prefix = Math.random() < 0.5 ? '34' : '37';
      length = 15;
      break;
    case 'Discover':
      prefix = Math.random() < 0.5 ? '6011' : getRandomNumber(6500, 6599).toString();
      length = 16;
      break;
  }

  const remainingLength = length - prefix.length;
  const randomDigits = Array.from({ length: remainingLength }, () => getRandomNumber(0, 9)).join('');
  return prefix + randomDigits;
};

const generateDataset = (count, issuer, includeName, includeAddress, includeCountry, includeZip) => {
  return Array.from({ length: count }, () => {
    const record = {
      cardNumber: generateCardNumber(issuer),
      issuer,
      expiryDate: getRandomExpiryDate(),
      cvv: generateCVV(issuer),
    };

    if (includeName) record.name = getRandomName();
    if (includeAddress) record.address = getRandomStreet();
    if (includeZip) record.zipcode = getRandomZip();
    if (includeCountry) record.country = getRandomCountry();

    return record;
  });
};

