// Extracting clock time data from queries
const data = queries.getClockTimeForStats.data;

// Retrieving user's preference for chart display from dropdown
const chartPreference = components.dropdown1.value;

// Getting the current date using the moment library
const currentDate = moment();

// Function to calculate total hours from a given set of entries
function calculateTotalHours(entries) {
  let totalMilliseconds = 0;

  entries.forEach((entry) => {
    // Converting clock-in and clock-out times to moment objects
    const clockIn = moment(parseInt(entry.clock_in_time));
    const clockOut = moment(parseInt(entry.clock_out_time));

    // Accumulating total milliseconds between clock-in and clock-out times
    totalMilliseconds += clockOut.diff(clockIn);
  });

  // Converting total milliseconds to total hours and returning the result
  return totalMilliseconds / (1000 * 60 * 60);
}

// Function to generate chart data based on user's preference
function generateChartData(data, preference) {
  const chartData = [];

  if (preference === "week") {
    // Generating chart data for each day of the week
    const daysOfWeek = ["Sun", "Mon", "Tue", "Wed", "Thu", "Fri", "Sat"];
    daysOfWeek.forEach((day) => {
      // Filtering entries for the specific day
      const filteredEntries = data.filter(
        (entry) => moment(parseInt(entry.clock_in_time)).format("ddd") === day
      );

      // Calculating total hours for the day and adding to chartData
      const totalHours = calculateTotalHours(filteredEntries);
      chartData.push({ x: day, y: totalHours.toFixed(2) });
    });
  } else if (preference === "month") {
    // Generating chart data for each day of the current month
    const daysInMonth = currentDate.daysInMonth();
    for (let i = 1; i <= daysInMonth; i++) {
      // Filtering entries for the specific day of the month
      const currentDay = moment(currentDate).date(i);
      const filteredEntries = data.filter((entry) =>
        moment(parseInt(entry.clock_in_time)).isSame(currentDay, "day")
      );

      // Calculating total hours for the day and adding to chartData
      const totalHours = calculateTotalHours(filteredEntries);
      chartData.push({
        x: currentDay.format("D MMM"),
        y: totalHours.toFixed(2),
      });
    }
  } else if (preference === "quarter") {
    // Generating chart data for each month of the current quarter
    const currentQuarter = currentDate.quarter();
    const monthsOfQuarter = [];
    for (let i = 0; i < 3; i++) {
      const monthIndex = (currentQuarter - 1) * 3 + i;

      // Filtering entries for the specific month of the quarter
      const filteredEntries = data.filter(
        (entry) => moment(parseInt(entry.clock_in_time)).month() === monthIndex
      );

      // Calculating total hours for the month and adding to chartData
      const totalHours = calculateTotalHours(filteredEntries);
      monthsOfQuarter.push({
        x: moment().month(monthIndex).format("MMM"),
        y: totalHours.toFixed(2),
      });
    }

    // Adding the quarterly data to chartData
    chartData.push(...monthsOfQuarter);
  }

  // Returning the final chart data
  return chartData;
}

// Generating chart data based on user's preference and returning the result
const result = generateChartData(data, chartPreference);
return result;

// Extracting clock time data for work hours calculations
const workHoursData = queries.getClockTimeForStats.data;

// Function to calculate total work hours from a given set of entries
function calculateTotalWorkHours(entries) {
  let totalMilliseconds = 0;

  // Iterating through entries and accumulating total milliseconds between clock-in and clock-out times
  entries.forEach((entry) => {
    const clockIn = moment(parseInt(entry.clock_in_time));
    const clockOut = moment(parseInt(entry.clock_out_time));
    totalMilliseconds += clockOut.diff(clockIn);
  });

  // Converting total milliseconds to total hours and returning the result
  return totalMilliseconds / (1000 * 60 * 60);
}

// Getting the current date using the moment library
const currentDate = moment();

// Determining the start date based on the user's dropdown selection
const startDate = currentDate
  .clone()
  .startOf(components.dropdown1.value)
  .subtract(1, "day");

// Determining the end date based on the user's dropdown selection
const endDate = currentDate.clone().endOf(components.dropdown1.value);

// Calculating the difference in days and weeks between the start and end dates
const daysDifference = endDate.diff(startDate, "days");
const weeksDifference = daysDifference / 7;

// Calculating the total work hours for the given data
const totalWorkHours = calculateTotalWorkHours(workHoursData);

// Calculating the average weekly work hours and rounding to 2 decimal places
const avgWeeklyWorkHours = (totalWorkHours / weeksDifference).toFixed(2);

// Returning the total work hours and average weekly work hours as an object
return { totalWorkHours, avgWeeklyWorkHours };

