// Function to convert base64 image data to a downloadable file
function convertImageUsingBase64(
  base64Data,
  filename,
  quality,
  dimension,
  outputFormat
) {
  // Decode the base64 string to a Blob object
  const base64String = base64Data.split(";base64,").pop();
  const byteCharacters = atob(base64String);
  const byteNumbers = new Array(byteCharacters.length);

  // Convert the byte characters to numbers
  for (let i = 0; i < byteCharacters.length; i++) {
    byteNumbers[i] = byteCharacters.charCodeAt(i);
  }

  // Create a Uint8Array from the byte numbers
  const byteArray = new Uint8Array(byteNumbers);
  // Create a Blob from the Uint8Array with the specified output format
  const blob = new Blob([byteArray], { type: outputFormat });
  // Create a URL for the Blob
  const url = URL.createObjectURL(blob);

  // Create an Image object to load the blob URL
  const img = new Image();

  // When the image is loaded, perform the following actions
  img.onload = function () {
    // Calculate the new canvas dimensions based on the aspect ratio of the original image
    let newWidth, newHeight;
    if (img.naturalWidth > img.naturalHeight) {
      newWidth = img.naturalWidth * dimension;
      newHeight = img.naturalHeight * (newWidth / img.naturalWidth);
    } else {
      newHeight = img.naturalHeight * dimension;
      newWidth = img.naturalWidth * (newHeight / img.naturalHeight);
    }

    // Create a canvas with the calculated dimensions
    const canvas = document.createElement("canvas");
    canvas.width = newWidth;
    canvas.height = newHeight;
    const ctx = canvas.getContext("2d");

    // Draw the image at its natural size
    ctx.drawImage(
      img,
      0,
      0,
      img.naturalWidth,
      img.naturalHeight,
      0,
      0,
      newWidth,
      newHeight
    );

    // Convert the canvas content to the specified format with quality
    const compressedDataURL = canvas.toDataURL(outputFormat, quality);

    // Create a temporary link to initiate the download
    const link = document.createElement("a");
    link.href = compressedDataURL;
    // Append the file extension based on the output format
    link.download = filename + "." + outputFormat.split("/")[1];
    // Append the link to the document body
    document.body.appendChild(link);
    // Click the link to start the download
    link.click();
    // Remove the link from the document body
    document.body.removeChild(link);

    // Clean up by revoking the blob URL to free memory
    URL.revokeObjectURL(url);
  };

  // Set the source of the image to the blob URL
  img.src = url;
}

