options.code:
totalQty = 0;
inStockQty = 0;
lowInStock = 0;
outOfStock = 0;
yetToReceive = 0;

queries.getProducts.data.forEach((product) => {
  totalQty += product.quantity;
  inStockQty += product.status == "In stock" ? product.quantity : 0;
  yetToReceive += product.status == "Yet to receive" ? product.quantity : 0;
  lowInStock += product.quantity <= 10 && product.quantity > 0 ? 1 : 0;
  outOfStock += product.quantity == 0 ? 1 : 0;
});

return {
  quantityInTotal: totalQty,
  quantityInStock: inStockQty,
  quantityYetToReceive: yetToReceive,
  lowInStock: lowInStock,
  outOfStock: outOfStock,
};

