function generateInvoice(invoiceDetails, items) {
  const {
    orderId,
    orderDate,
    companyName,
    companyAddress,
    companyLogo,
    customerName,
    customerAddress,
    taxPercentage,
    discount,
    issueDate,
    billPaid,
    dueDate,
    paymentDate,
    notes,
  } = invoiceDetails;

  let subTotal = 0;
  const itemRows = items.map((item) => {
    const amount = item.rate * item.quantity;
    subTotal += amount;
    return `
      <tr>
        <td>${item.description}</td>
        <td class="text-right">$${item.rate.toLocaleString("en-US", {
          minimumFractionDigits: 2,
          maximumFractionDigits: 2,
        })}</td>
        <td class="text-right">${item.quantity}</td>
        <td class="text-right">$${amount.toLocaleString("en-US", {
          minimumFractionDigits: 2,
          maximumFractionDigits: 2,
        })}</td>
      </tr>
    `;
  });

  const taxAmount = subTotal * (taxPercentage / 100);
  const totalAmount = subTotal + taxAmount - discount;

  const invoiceHtml = `
    <div class="invoice-container">
      <div class="invoice-header">
        <div class="invoice-title">
          <h2 class="invoice-header-h2">INVOICE</h2>
          <div class="invoice-order-data">
            <p><strong>#${orderId}</strong></p>
          </div>
        </div>
        <div class="invoice-title">
          ${companyLogo ? companyLogo : ""}
        </div>
      </div>
      <div class="seller-and-client-details">
        <div class="seller-details">
          <p><strong>${companyName}</strong></p>
          <p>${companyAddress}</p>
        </div>
        <div class="client-details">
          <p><strong>${customerName}</strong></p>
          <p>${customerAddress}</p>
        </div>
      </div>
      <table>
        <thead>
          <tr>
            <th>Deliverable</th>
            <th class="text-right">Rate</th>
            <th class="text-right">Qty</th>
            <th class="text-right">Amount</th>
          </tr>
        </thead>
        <tbody>
          ${itemRows.join("")}
        </tbody>
        <tfoot class="no-border">
          <tr>
            <td colspan="3" class="text-right"><strong>Subtotal</strong></td>
            <td class="text-right">$${subTotal.toLocaleString("en-US", {
              minimumFractionDigits: 2,
              maximumFractionDigits: 2,
            })}</td>
          </tr>
          <tr>
            <td colspan="3" class="text-right"><strong>Tax (${taxPercentage}%)</strong></td>
            <td class="text-right">$${taxAmount.toLocaleString("en-US", {
              minimumFractionDigits: 2,
              maximumFractionDigits: 2,
            })}</td>
          </tr>
          <tr>
            <td colspan="3" class="text-right"><strong>Discount</strong></td>
            <td class="text-right">$${discount.toLocaleString("en-US", {
              minimumFractionDigits: 2,
              maximumFractionDigits: 2,
            })}</td>
          </tr>
          <tr>
            <td colspan="3" class="text-right"><strong>Total amount</strong></td>
            <td class="text-right">$${totalAmount.toLocaleString("en-US", {
              minimumFractionDigits: 2,
              maximumFractionDigits: 2,
            })}</td>
          </tr>
        </tfoot>
      </table>
    </div>
    <div class="invoice-footer-container">
      <div class="footer-notes">
        <p><strong>Notes:</strong> ${notes}</p>
      </div>
      <div class="invoice-footer">
        <div class="invoice-details">
          <h2>Invoice Details</h2>
          <p>Date Issued: ${issueDate}</p>
          <p>${
            billPaid ? `Payment date: ${paymentDate}` : `Due Date: ${dueDate}`
          }</p>
        </div>
        <div class="total-amount">
          <h2>Total amount</h2>
          <p><h1>$${totalAmount.toLocaleString("en-US", {
            minimumFractionDigits: 2,
            maximumFractionDigits: 2,
          })}</h1></p>
        </div>
      </div>
    </div>
  `;

  const winPrint = window.open("", "", "width=595,height=842");

  const styles = document.querySelectorAll("link, style");
  let stylesHtml = "";
  for (let i = 0; i < styles.length; i++) {
    stylesHtml += styles[i].outerHTML;
  }
  stylesHtml += `
    <style>
      @page {
        size: portrait;
        margin: 0;
      }

      body {
        margin: 0;
        font-family: Arial, sans-serif;
      }

      .invoice-container {
        width: 100%;
        padding: 50px;
      }

      .seller-and-client-details,
      .totals {
        margin-bottom: 20px;
      }

      .invoice-header {
        margin-bottom: 20px;
        display: flex;
        justify-content: space-between;
      }

      .invoice-order-data {
        margin: 10px 0;
        color: #007bff;
        text-align: left;
      }

      .invoice-title {
        text-align: right;
      }

      .seller-and-client-details {
        display: flex;
        justify-content: space-between;
      }

      table {
        width: 100%;
        border-collapse: separate;
        border-spacing: 0;
        margin-bottom: 20px;
        border-radius: 10px;
        overflow: hidden;
      }

      th,
      td {
        padding: 10px 15px;
        border-width: 0 1px 1px 0;
        border-style: solid;
        border-color: #ddd;
      }

      th {
        background-color: #f5f5f5;
      }

      tr th {
        border-top-width: 1px;
      }

      tr th:first-of-type,
      tr td:first-of-type {
        border-left-width: 1px;
      }

      tr:first-child th:first-child {
        border-top-left-radius: 10px;
      }

      tr:first-child th:last-child {
        border-top-right-radius: 10px;
      }

      tr:last-child td:first-child {
        border-bottom-left-radius: 10px;
      }

      tr:last-child td:last-child {
        border-bottom-right-radius: 10px;
      }

      .text-right {
        text-align: right;
      }

      .totals {
        text-align: right;
        margin-right: 10px;
      }

      .totals p {
        padding: 5px;
        font-size: 16px;
      }

      .footer-notes {
        padding: 20px 50px;
      }

      .invoice-footer-container {
        width: 100%;
        padding: 0;
        margin: 0;
        position: fixed;
        bottom: 0;
      }

      .invoice-footer {
        display: flex;
        justify-content: space-between;
        align-items: flex-start;
        background-color: #007bff;
        color: #fff;
        padding: 50px;
        width: 100%;
      }

      .total-amount {
        text-align: right;
      }

      h1 {
        font-size: 40px;
      }

      .invoice-header-h2 {
        margin: 0;
        padding: 0;
        line-height: 1;
      }

      h2 {
        font-size: 20px;
      }

      p {
        margin: 0;
        padding: 0;
      }

      .invoice-details p {
        line-height: 1.5;
      }

      .no-border td {
        border: none;
        padding-top: 8px;
        padding-bottom: 8px;
      }

      tfoot::before {
        content: "";
        display: block;
        height: 16px;
      }
    </style>
  `;

  winPrint.document.write(`
    <html>
      <head>
        ${stylesHtml}
        <meta charset="UTF-8">
        <meta name="viewport" content="width=device-width, initial-scale=1.0">
      </head>
      <body>
        ${invoiceHtml}
      </body>
    </html>
  `);

  winPrint.document.title = `Invoice #${orderId}`;
  winPrint.document.close();
  winPrint.focus();
  winPrint.print();
  winPrint.close();
}

const invoiceDetails = {
  orderId: queries.getOrderDetails.data.id,
  orderDate: moment(queries.getOrderDetails.data.billing_date).format(
    "Do MMM YYYY"
  ),
  companyName: "ToolJet",
  companyAddress: "San Francisco, CA",
  companyLogo: queries.logoSvgCode.data,
  customerName: queries.getOrderDetails.data.customer_name,
  customerAddress: queries.getOrderDetails.data.customer_address,
  taxPercentage: queries.getOrderDetails.data.tax_percentage,
  discount: queries.getOrderDetails.data.discount,
  issueDate: moment(queries.getOrderDetails.data.billing_date).format(
    "Do MMM YYYY"
  ),
  billPaid: queries.getOrderDetails.data.bill_paid,
  dueDate: moment(queries.getOrderDetails.data.billing_date)
    .add(15, "days")
    .format("Do MMM YYYY"),
  paymentDate: queries.getOrderDetails.data.payment_date
    ? moment(queries.getOrderDetails.data.payment_date).format("Do MMM YYYY")
    : "",
  notes: queries.getOrderDetails.data.bill_paid
    ? "Thank you for your business."
    : "Pay within 15 days. Thank you for your business.",
};

const items = queries.getOrderItems.data.map(
  ({ item_name, rate, quantity, ...row }) => ({
    description: item_name,
    rate,
    quantity,
  })
);

generateInvoice(invoiceDetails, items);
