var totalSignups = 0;
var totalUsersCreatedApps = 0;
var totalUsersReleasedApps = 0;

data.forEach((row) => {
  totalSignups += 1;
  if (row.first_app_created_on != null) {
    totalUsersCreatedApps += 1;
  }
  if (row.first_app_released_on != null) {
    totalUsersReleasedApps += 1;
  }
});

return { totalSignups, totalUsersCreatedApps, totalUsersReleasedApps };

var totalSignups = 0;
var signupByChannel = {};
var totalSubscribed = 0;

data.forEach((row) => {
  totalSignups += 1;

  if (!(row.redirected_from in signupByChannel)) {
    signupByChannel[row.redirected_from] = 0;
  }
  signupByChannel[row.redirected_from] += 1;

  if (
    (components.acquisitionDropdown.value == "*" &&
      row.subscription_starts_on != null) ||
    (components.acquisitionDropdown.value != "*" &&
      moment(row.subscription_starts_on).isSameOrAfter(
        moment().startOf(components.acquisitionDropdown.value).valueOf()
      ))
  ) {
    totalSubscribed += 1;
  }
});

return { totalSignups, signupByChannel, totalSubscribed };

var totalReferrals = 0;

data.forEach((row) => {
  totalReferrals += row?.referred_users_count ?? 0;
});

return { totalReferrals };

var chartData = [];

if (data.length > 0) {
  var preStart = 0;

  while (
    data.length > 0 &&
    moment(parseInt(data[0].signed_up_on)).isBefore(
      moment().startOf(components.retentionDropdown.value)
    )
  ) {
    preStart += 1;
    data.shift();
  }

  function getDates(type, date, format) {
    const startDate = moment(date).startOf(type);
    const endDate = moment(startDate).endOf(type);
    const jump = { month: "day", quarter: "month", year: "month" }[type];
    var dateWiseCount = {};
    var dates = [];

    dates.push(startDate.format(format));
    dateWiseCount[startDate.format(format)] = preStart ?? 0;
    startDate.add(1, jump);

    while (startDate.isSameOrBefore(endDate)) {
      dates.push(startDate.format(format));
      dateWiseCount[startDate.format(format)] = 0;
      startDate.add(1, jump);
    }

    return { dates, dateWiseCount };
  }

  const type = components.retentionDropdown.value;
  const date = moment().startOf(components.retentionDropdown.value).valueOf();
  const format = { month: "Do MMM", quarter: "MMM", year: "MMM" }[type];
  const { dates, dateWiseCount } = getDates(type, date, format);

  data.forEach((row) => {
    const startDate = moment(parseInt(row.signed_up_on)).format(format);
    const endDate = moment(row.last_active_on).format(format);
    var startIndex = dates.indexOf(startDate);
    var endIndex = dates.indexOf(endDate);
    const tempDates = dates.slice(startIndex, endIndex + 1);
    tempDates.forEach((date) => {
      dateWiseCount[date] += 1;
    });
  });

  chartData = dates.map((date) => ({ x: date, y: dateWiseCount[date] }));
}

return { chartData };

