options.code:
function countDays(startDate, endDate) {
  let sunCount = 0;
  let monCount = 0;
  let tueCount = 0;
  let wedCount = 0;
  let thuCount = 0;
  let friCount = 0;
  let satCount = 0;

  let currentDate = moment(startDate);
  while (currentDate.isBefore(endDate)) {
    switch (currentDate.day()) {
      case 0:
        sunCount++;
        break;
      case 1:
        monCount++;
        break;
      case 2:
        tueCount++;
        break;
      case 3:
        wedCount++;
        break;
      case 4:
        thuCount++;
        break;
      case 5:
        friCount++;
        break;
      case 6:
        satCount++;
        break;
    }

    currentDate.add(1, 'days');
  }

  return {
    Sun: sunCount,
    Mon: monCount,
    Tue: tueCount,
    Wed: wedCount,
    Thu: thuCount,
    Fri: friCount,
    Sat: satCount,
  };
}

const startDate = moment(components.calendar1.selectedEvent.start);
const endDate = moment(components.calendar1.selectedEvent.end);
const dayCounts = countDays(startDate, endDate);
return dayCounts;
