// query runs when row is selected and data is changed for edit menu
components.editTitle.value = components.table1.selectedRow.library_resources_title 
components.editAuthor.value = components.table1.selectedRow.library_resources_author 
components.editFormat.value = components.table1.selectedRow.library_resources_format
components.editAvailability.value = components.table1.selectedRow.library_resources_availability
components.datepicker1.value = components.table1.selectedRow.library_resources_publication_date
components.textarea1.value = components.table1.selectedRow.library_resources_description


// checks if row is not selected then changes the return a title menu to issue a title
if(components.table1.selectedRowId == null ||components.table1.selectedRow.library_resources_availability == "AVAILABLE")
{
  variables.issueHeading = "Issue a Title" 
  variables.issueButton = "Issue";
}
else{
  variables.issueHeading = "Return a Title" 
  variables.issueButton= "Return";
}

let tableData = queries.getBooksDetails.data.result
let authors = components.authorFilter.values;  // Array of selected authors
let formats = components.formatFilter.values;  // Array of selected formats

// Update the table data only if there are selected authors or formats
if (authors.length > 0 || formats.length > 0) {
    let filteredData = tableData.filter(item => {
    // Split the author string by commas and trim whitespace
    let itemAuthors = item.library_resources_author.split(',').map(author => author.trim());

    // Check if all selected authors exist in the itemAuthors array (book can have additional authors)
    const matchesAuthor = authors.length === 0 || authors.every(selectedAuthor => itemAuthors.includes(selectedAuthor));
    // Check if the format matches
    const matchesFormat = formats.length === 0 || formats.every(selectedFormat => item.library_resources_format.includes(selectedFormat));

    // Return true only if both the author condition and the format condition are met
    return matchesAuthor && matchesFormat;
  });
    components.table1.currentData = filteredData;
} else {
    // Reset the table data by running the query when no filters are applied
    components.table1.currentData = queries.setTableData.run();
}

//column row is selected or not
if(components.table1.selectedRowId == null){
  actions.showAlert('error',"Table row is not selected!");
}
else if (!components.editTitle.value || 
    !components.editAuthor.value || 
    !components.textarea1.value || 
    !components.editFormat.value || 
    !components.datepicker1.value) {
    actions.showAlert('error',"Please fill out all the required fields.");

} else {
    await queries.editBookDetails.run();
    actions.showAlert('success',"Book details updated!!");
}

if (!components.newTitle.value || 
    !components.newAuthor.value || 
    !components.textarea2.value || 
    !components.newFormat.value || 
    !components.newDatePicker.value) {
    actions.showAlert('error',"Please fill out all the required fields.");

} else {
    await queries.addBookDetails.run();
    actions.showAlert('success',"Book details added!!");
}

if(!components.form1.data.textinput1.value || !components.form1.data.dropdown1.value)
{
  actions.showAlert('error','Please enter required fields')
}
else{
  if(variables.issueButton === "Issue")
  {
    if(components.table1.currentData.find(data => data.library_resources_title === components.form1.data.dropdown1.value).borrowers_details_return_date)
    {
      await queries.issueOldTitle.run()
    }
    else{
      await queries.issueNewTitle.run()
    }
   }
  else{
    if(components.table1.currentData.find(data => data.borrowers_details_borrower_name === components.form1.data.textinput1.value))
    {
      await queries.returnATitle.run();
    }
    else{
      actions.showAlert('error','Borrower name does not match')
    }
   }
}

let availability = components.table1.selectedRow.library_resources_availability;

if(availability === "ISSUED")
{
  components.form1.data.dropdown1.value = components.table1.selectedRow.library_resources_title;
  components.form1.data.textinput1.value = components.table1.selectedRow.borrowers_details_borrower_name;
}
else{
  components.form1.data.dropdown1.value = components.table1.selectedRow.library_resources_title;
  components.form1.data.textinput1.value = "";
}
