  "Technologies", "Solutions", "Innovations", "Systems", "Enterprises", "Global", "International", "Group", "Consulting", "Services",
  "Dynamics", "Digital", "Networks", "Ventures", "Partners", "Enterprises", "Solutions", "Industries", "Labs", "Strategies"
];

// Pattern for the URL
const urlPattern = "https://www.";

let companyUrls = "";
for (let i = 0; i < parameters.count; i++) {

    let firstNameObj = await queries.getRandomItem.run({arr: firstCompanyNames})
    let firstName = `${firstNameObj.data}`;

  	let secondNameObj = await queries.getRandomItem.run({arr: secondCompanyNames})
    let secondName = `${secondNameObj.data}`;

    let companyName = `${firstName}${secondName}`;

    // Replace spaces with hyphens and convert to lowercase for URL
    let urlFriendlyName = companyName.replace(/\s+/g, '-').toLowerCase();

    // Construct the company URL
    let companyUrl = `${urlPattern}${urlFriendlyName}.com, `;

    companyUrls += companyUrl;
}

return companyUrls;

