var amt =
  components.numberinput1.value -
  (components.numberinput2.value * components.numberinput1.value) / 100;
var apr = components.numberinput3.value / 1200;
var term = components.numberinput4.value * 12;
var payment = (
  (amt * (apr * Math.pow(1 + apr, term))) /
  (Math.pow(1 + apr, term) - 1)
).toFixed(2);
actions.setVariable("monthly", payment);
return payment;
