const allData = queries.getEarningsAndExpenses.getData();

function calculateCurrentMonthTotals(data) {
  const currentMonth = moment().format("YYYY MM");

  let totalEarnings = 0;
  let totalExpenses = 0;

  data.forEach((entry) => {
    if (moment(entry.date_of_transaction).format("YYYY MM") === currentMonth) {
      totalEarnings +=
        entry.transaction_type == "Earning" ? entry.transaction_amount : 0;
      totalExpenses +=
        entry.transaction_type == "Expense" ? entry.transaction_amount : 0;
    }
  });

  return {
    earnings: totalEarnings,
    expenses: totalExpenses,
  };
}

const currentMonthTotals = calculateCurrentMonthTotals(allData);
return currentMonthTotals;
