  .filter((row) => row.id != variables.rowIdToDelete)
  .map((row, n) => ({ ...row, id: n }));

return Object.keys(components.listview1.data)
  .map((row) => parseInt(row))
  .toSorted((a, b) => a - b)
  .slice(0, variables.tableColumns.length)
  .map((row) => ({
    id: row,
    column_name: components.listview1.data[row].column_name.value,
    column_type: components.listview1.data[row].column_type.value,
    length: components.listview1.data[row].length.value,
    default_value: components.listview1.data[row].default_value.value,
    nullable: components.listview1.data[row].nullable.value,
  }));

const columns = variables.currentTableData;
const tableName = components.textinput1.value;

let query = `CREATE TABLE ${tableName} (`;

columns.forEach((column, index) => {
  let columnDefinition = `\n\t"${column.column_name}" ${column.column_type}`;
  if (column.column_type === "varchar" && column.length) {
    columnDefinition += `(${column.length})`;
  }
  if (
    !["undefined", "bigserial", "serial", "uuid"].includes(column.column_type)
  ) {
    if (!column.nullable) {
      columnDefinition += ` NOT NULL`;
    }

    if (column.default_value !== "") {
      if (
        ["varchar", "text", "timestamp", "timestamptz"].includes(
          column.column_type
        )
      ) {
        const escapedDefaultValue = column.default_value.replace(/'/g, "''");
        columnDefinition += ` DEFAULT '${escapedDefaultValue}'`;
      } else {
        columnDefinition += ` DEFAULT ${column.default_value}`;
      }
    }
  }
  query += `${columnDefinition}`;
  if (index < columns.length - 1) {
    query += `,`;
  }
});

query += `\n);`;

return { query };

