    column_name: components.listview2.data[row].add_column_name.value,
    column_type: components.listview2.data[row].add_column_type.value,
    length: components.listview2.data[row].add_length.value,
    default_value: components.listview2.data[row].add_default_value.value,
    nullable: components.listview2.data[row].add_nullable.value,
  }));

const columnsToAdd = variables.currentTableData;
const tableName = components.textinput4.value;
const columnsToRemove = Object.keys(components.listview3.data)
  .filter((row) => !components.listview3.data[row].include.value)
  .toSorted((a, b) => a - b)
  .map((row) => components.listview3.data[row].current_column_name.text);

let query = `ALTER TABLE ${tableName}`;

columnsToRemove.forEach((column, index) => {
  let columnDefinition = `\n\tDROP COLUMN "${column}"`;
  query += `${columnDefinition}`;
  if (index < columnsToRemove.length - 1 || columnsToAdd.length > 0) {
    query += `,`;
  }
});

columnsToAdd.forEach((column, index) => {
  let columnDefinition = `\n\tADD COLUMN "${column.column_name}" ${column.column_type}`;
  if (column.column_type === "varchar" && column.length) {
    columnDefinition += `(${column.length})`;
  }
  if (
    !["undefined", "bigserial", "serial", "uuid"].includes(column.column_type)
  ) {
    if (!column.nullable) {
      columnDefinition += ` NOT NULL`;
    }

    if (column.default_value !== "") {
      if (
        ["varchar", "text", "timestamp", "timestamptz"].includes(
          column.column_type
        )
      ) {
        const escapedDefaultValue = column.default_value.replace(/'/g, "''");
        columnDefinition += ` DEFAULT '${escapedDefaultValue}'`;
      } else {
        columnDefinition += ` DEFAULT ${column.default_value}`;
      }
    }
  }
  query += `${columnDefinition}`;
  if (index < columnsToAdd.length - 1) {
    query += `,`;
  }
});

query += `;`;

return { query };

const finalData = [];

queries.getTableSchema.data.forEach((row, index) => {
  let rowDetails = {
    id: index,
    column_name: row.column_name,
    column_type: "",
    default_value: "",
    nullable: row.is_nullable == "YES",
  };

  switch (row.data_type) {
    case "bigint":
      if (
        row.column_default ==
        `nextval('${row.table_name}_${row.column_name}_seq'::regclass)`
      ) {
        rowDetails.column_type = "bigserial";
        rowDetails.default_value = null;
      } else {
        rowDetails.column_type = "bigint";
        rowDetails.default_value = row.column_default;
      }
      break;

    case "boolean":
      rowDetails.column_type = "boolean";
      rowDetails.default_value = row.column_default;
      break;

    case "real":
      rowDetails.column_type = "float4";
      rowDetails.default_value = row.column_default;
      break;

    case "double precision":
      rowDetails.column_type = "float8";
      rowDetails.default_value = row.column_default;
      break;

    case "integer":
      if (
        row.column_default ==
        `nextval('${row.table_name}_${row.column_name}_seq'::regclass)`
      ) {
        rowDetails.column_type = "serial";
        rowDetails.default_value = null;
      } else {
        rowDetails.column_type = "integer";
        rowDetails.default_value = row.column_default;
      }
      break;

    case "numeric":
      rowDetails.column_type = "numeric";
      rowDetails.default_value = row.column_default;
      break;

    case "text":
      rowDetails.column_type = "text";
      rowDetails.default_value = row.column_default
        ? row.column_default.match(/^'(.+)'::text$/)[1]
        : null;
      break;

    case "timestamp without time zone":
      rowDetails.column_type = "timestamp";
      rowDetails.default_value = row.column_default
        ? row.column_default.match(/^'(.+)'::timestamp without time zone$/)[1]
        : null;
      break;

    case "timestamp with time zone":
      rowDetails.column_type = "timestamptz";
      rowDetails.default_value = row.column_default
        ? row.column_default.match(/^'(.+)'::timestamp with time zone$/)[1]
        : null;
      break;

    case "uuid":
      rowDetails.column_type = "uuid";
      rowDetails.default_value = row.column_default;
      break;

    case "character varying":
      rowDetails.column_type = "varchar";
      rowDetails.default_value = row.column_default
        ? row.column_default.match(/^'(.+)'::character varying$/)[1]
        : null;
      break;
  }

  finalData.push(rowDetails);
});

return finalData;

