const data = queries.getPromoCodes.data;
let segregatedData = { active: [], inactive: [] };
const currentDate = moment().startOf("day");

function stringifyValue(discountType, amount) {
  var value = "";
  if (discountType == "Percentage") {
    value = amount.toString() + "%";
  } else if (discountType == "Fixed") {
    value = "$" + amount.toString();
  } else {
    value = amount.toString();
  }
  return value;
}

data.forEach((codeData) => {
  if (moment(codeData.valid_till, "YYYY/MM/DD").isSameOrAfter(currentDate)) {
    segregatedData.active.push({
      ...codeData,
      discount_value: stringifyValue(codeData.discount_type, codeData.amount),
      free_shipping_bool: codeData.free_shipping,
      free_shipping: { true: "Yes", false: "No" }[codeData.free_shipping],
    });
  } else {
    segregatedData.inactive.push({
      ...codeData,
      discount_value: stringifyValue(codeData.discount_type, codeData.amount),
      free_shipping_bool: codeData.free_shipping,
      free_shipping: { true: "Yes", false: "No" }[codeData.free_shipping],
    });
  }
});

return segregatedData;

function generatePromoCode() {
    const characters = 'ABCDEFGHIJKLMNOPQRSTUVWXYZ0123456789';
    const codeLength = 15;
    let promoCode = '';

    for (let i = 0; i < codeLength; i++) {
        const randomIndex = Math.floor(Math.random() * characters.length);
        promoCode += characters[randomIndex];
    }

    return promoCode;
}

return {promoCode: generatePromoCode()};

if (components.dropdown3.value === undefined) {
  finalValue = 0;
  return { finalValue };
}

const promoCode = queries.getPromoCodes.data.filter(
  (code) => code.id == components.dropdown3.value
)[0];

const cartValue = components.numberinput2.value;

var finalValue = 0;

if (promoCode.minimum_spend !== null && cartValue < promoCode.minimum_spend) {
  finalValue = cartValue;
} else if (promoCode.discount_type === "percent") {
  if (promoCode.maximum_spend !== null && cartValue > promoCode.maximum_spend) {
    finalValue = cartValue - (promoCode.amount / 100) * promoCode.maximum_spend;
  } else {
    finalValue = cartValue - (promoCode.amount / 100) * cartValue;
  }
} else {
  if (promoCode.maximum_spend !== null && cartValue > promoCode.maximum_spend) {
    finalValue =
      cartValue - Math.min(promoCode.amount, promoCode.maximum_spend);
  } else {
    finalValue = cartValue - Math.min(promoCode.amount, cartValue);
  }
}

finalValue += promoCode.free_shipping ? 0 : 10;
return { finalValue };
