data = queries.getOrders.data;

months = moment.monthsShort();

dataPerMonth = months
  .map((month) => ({
    [month]: {
      monthName: month,
      qtySold: 0,
      revenue: 0,
      avgOrderValue: 0,
      customerIds: new Set(),
    },
  }))
  .reduce((a, b) => ({ ...a, ...b }));

dataPerCountry = {};

data.forEach((order) => {
  if (moment(order.created_at).format("YYYY") == moment().format("YYYY")) {
    mmm = moment(order.created_at).format("MMM");
    dataPerMonth[mmm].qtySold += order.quantity;
    dataPerMonth[mmm].revenue = parseFloat(
      (dataPerMonth[mmm].revenue + order.total_price).toFixed(2)
    );
    dataPerMonth[mmm].avgOrderValue = parseFloat(
      (dataPerMonth[mmm].revenue / dataPerMonth[mmm].qtySold).toFixed(2)
    );
    dataPerMonth[mmm].customerIds.add(order.customer_id);
    if (dataPerCountry.hasOwnProperty(order.country)) {
      dataPerCountry[order.country].revenue = parseFloat(
        (dataPerCountry[order.country].revenue + order.total_price).toFixed(2)
      );
      dataPerCountry[order.country].qtySold += order.quantity;
    } else {
      dataPerCountry[order.country] = {
        countryName: order.country,
        revenue: parseFloat(order.total_price.toFixed(2)),
        qtySold: order.quantity,
      };
    }
  }
});

dataPerCountry = Object.values(dataPerCountry);

format = {
  data: [
    {
      type: "pie",
      labels: dataPerCountry.map((country) => country.countryName),
      values: dataPerCountry.map((country) =>
        parseFloat(country.revenue.toFixed(2))
      ),
      textinfo: "label+percent",
      textposition: "inside",
      textfont: {
        color: "#ffffff",
        size: 14,
      },
      marker: {
        colors: [],
      },
    },
  ],
  layout: {
    title: "Sales per country this year",
    showlegend: false,
    height: 500,
    width: 700,
    margin: {
      l: 50,
      r: 50,
      t: 50,
      b: 50,
    },
  },
};

return { dataPerMonth: dataPerMonth, chartData: format };

function generateShadesOfColor(baseColor, numShades) {
  const match = baseColor.match(/^#?([a-f\d]{2})([a-f\d]{2})([a-f\d]{2})$/i);
  if (!match) {
    throw new Error(
      "Invalid color format. Please use hexadecimal color notation."
    );
  }

  const [, r, g, b] = match.map((hex) => parseInt(hex, 16));

  const shades = [];
  for (let i = 0; i < numShades; i++) {
    const newR = Math.floor(r * (1 - i / numShades));
    const newG = Math.floor(g * (1 - i / numShades));
    const newB = Math.floor(b * (1 - i / numShades));

    const shade = `#${newR.toString(16).padStart(2, "0")}${newG
      .toString(16)
      .padStart(2, "0")}${newB.toString(16).padStart(2, "0")}`;
    shades.push(shade);
  }

  return shades;
}

data = queries.ordersAnalytics.data.chartData;
