let data;
if(parameters.event === "add_student" || parameters.event === "initialize_table" || parameters.event === "update_student"){
  await queries.page_load_get_students.run();
  data = queries.page_load_get_students.getData();
}
else if (parameters.event === "filter_student"){
  if(components.courses_multiselect.values.join(",") === "" && components.occupation_multiselect.values.length === 0)
  {
    await queries.page_load_get_students.run();
    data = queries.page_load_get_students.getData();
  }
  else if(components.occupation_multiselect.values.length === 0){
    await queries.get_students_courses_filter.run();
    data = queries.get_students_courses_filter.getData();
  }
  else if(components.courses_multiselect.values.join(",") === ""){
    await queries.get_students_occupation_filter.run();
    data = queries.get_students_occupation_filter.getData();
  }
  else{ 
    await queries.get_students.run();
    data = queries.get_students.getData();
  }
}


return data;

